mapper.select:
SELECT A.USER_NO , A.EMP_SERIAL , A.USER_NM , A.BIRTH_DT , A.GENDER_YN , B.DEPT_NM , C.JOB_NM , A.LINE_NO , A.PHONE_NO , A.ADDRESS , A.HIREDATE , A.INPUT_DT , A.ADMIN_YN , A.MANAGE_YN FROM T_EDU_USER A LEFT OUTER JOIN T_EDU_DEPT B ON (A.DEPT_NO = B.DEPT_NO) LEFT OUTER JOIN T_EDU_JOB C ON (A.JOB_NO = C.JOB_NO) WHERE 1=1
AND A.DEPT_NO = #{dept_no}
AND A.JOB_NO = #{job_no}
AND A.ADMIN_YN = #{admin_yn}
AND A.MANAGE_YN = #{manage_yn}
AND A.USER_NM LIKE CONCAT('%',CONCAT(#{searchWord},'%'))
ORDER BY A.USER_NO DESC

SELECT COUNT(*) FROM T_EDU_USER WHERE 1=1
AND dept_no = #{dept_no}
AND (user_nm LIKE CONCAT('%',CONCAT(#{searchWord},'%')))

mapper.insert:

	
	SELECT TO_CHAR(SYSDATE,'YYMM')||'-'||LPAD((NVL(MAX(SUBSTR(EMP_SERIAL, 6, 3)),0) + 1),3,'0')
	  FROM T_EDU_USER
	 WHERE HIREDATE BETWEEN TRUNC(SYSDATE,'MONTH') AND LAST_DAY(SYSDATE)
	


INSERT INTO T_EDU_USER (
	   USER_NO
	 , EMP_SERIAL
	 , USER_NM
	 , BIRTH_DT
	 , GENDER_YN
	 , HIREDATE
	 , INPUT_DT
	 , JOB_NO
	 , DEPT_NO
	 , LINE_NO
	 , PHONE_NO
	 , SECOND_NO
	 , POST
	 , ADDRESS
	 , EMAIL
	 , MANAGE_YN
	 , ADMIN_YN
	 , PROFIL_PICTURE
) VALUES (
	   SEQ_EDU_USER.NEXTVAL
	 , #{emp_serial}
	 , #{user_nm}
	 , #{birth_dt}
	 , #{gender_yn}
	 , #{hiredate}
	 , sysdate
	 , #{job_no}
	 , #{dept_no}
	 , #{line_no}
	 , #{phone_no}
	 , #{second_no}
	 , #{post}
	 , #{address}
	 , #{email}
	 , #{manage_yn}
	 , #{admin_yn}
	 , #{profil_picture}
)

mapper.update:
	
	UPDATE T_EDU_USER SET
		   DEPT_NO = #{dept_no}
		 , JOB_NO = #{job_no}
		 , LINE_NO = #{line_no}
		 , PHONE_NO = #{phone_no}
		 , SECOND_NO = #{second_no}
		 , POST = #{post}
		 , ADDRESS = #{address}
		 , EMAIL = #{email}
		 
	
		
	  	, PROFIL_PICTURE = #{profil_picture}
   	
	
	
		 , ADMIN_YN = #{admin_yn}
		 , MANAGE_YN = #{manage_yn}
		 , TIME_STAMP = SYSDATE
	 WHERE USER_NO = #{user_no}
