mapper.insert:

	
	SELECT SEQ_EDU_ATTACH.NEXTVAL FROM DUAL
	


INSERT INTO T_EDU_ATTACH (
       ATTACH_NO
     , REF_NO
     , TABLE_NM
     , ATTACH_FILE
     , ATTACH_SEQ
) VALUES (
       #{attach_no}
     , #{ref_no}
     , #{table_nm}
     , #{attach_file}
     , (SELECT NVL(MAX(ATTACH_SEQ), 0) + 1
          FROM T_EDU_ATTACH
         WHERE TABLE_NM = #{table_nm}
           AND REF_NO = #{ref_no}
       )
)

mapper.update:

UPDATE T_EDU_ATTACH SET
       ATTACH_FILE = #{attach_file}
 WHERE TABLE_NM =  #{table_nm}
   AND EDU_NO = #{edu_no}
