mapper.insert:


	SELECT SEQ_EDU.NEXTVAL FROM DUAL



INSERT INTO T_EDU (
       EDU_NO
     , DEPT_NO
     , CATEGORY_NO
     , ESSENTIAL_YN
     , TITLE
     , CONTENTS
     , CONTENTS_TAG
     , PERIOD_ST
     , PERIOD_ED
     , USER_NO
) VALUES(
        #{edu_no}
      , #{dept_no}
      , #{category_no}
      , #{essential_yn}
      , #{title}
      ,	#{contents}
      , #{contents_tag}
      , #{period_st}
      , #{period_ed}
      , #{user_no}
)

mapper.update:

UPDATE T_EDU SET
       CATEGORY_NO = #{category_no}
     , ESSENTIAL_YN = #{essential_yn}
     , TITLE = #{title}
     , CONTENTS = #{contents}
     , PERIOD_ST = #{period_st}
     , PERIOD_ED = #{period_ed}
     , CONTENTS_TAG = #{contents_tag}
     , TIME_STAMP = SYSDATE
 WHERE EDU_NO = #{edu_no}

mapper.select:
select count(*) from t_edu where 1=1
AND dept_no = #{dept_no}
AND (title LIKE CONCAT('%',CONCAT(#{searchWord},'%')) OR contents LIKE CONCAT('%',CONCAT(#{searchWord},'%')))

SELECT * from t_edu WHERE edu_no IN (SELECT MAX(edu_no) FROM t_edu WHERE edu_no < #{seq_no} )
AND dept_no = #{dept_no}
AND (title LIKE CONCAT('%',CONCAT(#{searchWord},'%')) OR contents LIKE CONCAT('%',CONCAT(#{searchWord},'%')))

SELECT * from t_edu WHERE edu_no IN (SELECT MIN(edu_no) FROM t_edu WHERE edu_no > #{seq_no})
AND dept_no = #{dept_no}
AND (title LIKE CONCAT('%',CONCAT(#{searchWord},'%')) OR contents LIKE CONCAT('%',CONCAT(#{searchWord},'%')))
