mapper.select:
SELECT ROWNUM, QNA_NO, TITLE, QUESTION_DT, VISIT_NO, BOARD_GB, Q_CONTENTS FROM( SELECT QNA_NO, TITLE, QUESTION_DT, VISIT_NO, BOARD_GB, q_contents FROM T_EDU_QNA WHERE 1=1 and BOARD_GB=#{board_gb}
and (TITLE LIKE CONCAT('%',CONCAT(#{searchWord},'%')) OR Q_CONTENTS LIKE CONCAT('%',CONCAT(#{searchWord},'%')))
ORDER BY QNA_NO) ORDER BY ROWNUM DESC
