mapper.select:
SELECT TEST_NO , EDU_NO , GUBUN , Q_NO , QUESTION , ANSWER_NM , CORR_ANSWER , USER_NO , INPUT_DT FROM T_EDU_TEST WHERE EDU_NO = #{edu_no}
AND USER_NO = #{user_no}
ORDER BY Q_NO ASC

mapper.update:

UPDATE T_EDU_TEST SET
       GUBUN = #{gubun}
     , QUESTION = #{question}
     , ANSWER_NM = #{answer_nm}
     , CORR_ANSWER = #{corr_answer}
     , TIME_STAMP = SYSDATE
  WHERE TEST_NO = #{test_no}

mapper.insert:


	SELECT SEQ_EDU_TEST.NEXTVAL AS TEST_NO FROM DUAL
	


INSERT INTO T_EDU_TEST (
       TEST_NO
     , EDU_NO
     , GUBUN
     , Q_NO
     , QUESTION
     , ANSWER_NM
     , CORR_ANSWER
     , USER_NO
) VALUES (
	   #{test_no}
	 , 0
	 , #{gubun}
	 , (SELECT NVL(MAX(Q_NO), 0) + 1 AS Q_NO 
	      FROM T_EDU_TEST 
	     WHERE EDU_NO = #{edu_no}
	       AND USER_NO = #{user_no})
	 , #{question}
	 , #{answer_nm}
	 , #{corr_answer}
	 , #{user_no}
)

mapper.update:

UPDATE T_EDU_TEST SET
       EDU_NO = #{edu_no}
 WHERE EDU_NO = 0
   AND USER_NO = #{user_no}
